mapper.insert:
insert into emptrain (id, eid, trainDate, trainContent, remark)
values (#{id}, #{eid}, #{trainDate},#{trainContent}, #{remark}
       )

mapper.update:
update emptrain
set trainDate = #{trainDate},
    trainContent = #{trainContent},
    remark = #{remark}
where id = #{id} and eid = #{eid}
